mapper.select:
select t1.batch_no,t1.machine_id,t1.create_time,t2.machine_code, CONCAT( t4.province, t4.city, t4.district, if(t4.circle='其他','',t4.circle),IFNULL(t3.mall,""),IFNULL( t3.name,'') ) AS localeStr from inno72_supply_channel_history t1 left join inno72_machine t2 on t1.machine_id=t2.id LEFT JOIN inno72_locale t3 ON t3.id = t2.locale_id LEFT JOIN inno72_admin_area t4 ON t4.CODE = t3.area_code left join inno72_check_user_machine t5 on t2.id=t5.machine_id where t5.check_user_id=#{checkUserId}
and ( t2.machine_code like CONCAT('%','${keyword}','%') OR t4.circle like CONCAT('%','${keyword}','%') )
and t1.create_time between #{beginTime} and #{endTime}
group by t1.machine_id,t1.create_time,t1.batch_no order by t1.create_time desc
